package com.example.smcylerview

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.bumptech.glide.Glide
import com.example.smcylerview.databinding.ActivityActorinfoBinding

class Actorinfo : AppCompatActivity() {
    private lateinit var binding : ActivityActorinfoBinding
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityActorinfoBinding.inflate(layoutInflater)
        setContentView(binding.root)


        Glide.with(this)
            .load("data:image/png;base64,")
            .into(binding.smlogo)

        Glide.with(this)
            .load("https://www.smentertainment.com/Images/pageVisual/top_album_banner_0.jpg")
            .into(binding.banner)
        val image = intent.getStringExtra("image")
        val name = intent.getStringExtra("name")
        val info = intent.getStringExtra("info")
        val birth = intent.getStringExtra("birth")
        val debut = intent.getStringExtra("debut")

        Glide.with(binding.image.context)
            .load(image)
            .into(binding.image)

        if(birth == ""){
            binding.birth1.setText("")
        }
        else{
            binding.birth.text = birth
        }
        binding.name.text = name
        binding.info.text = info

        if(debut == ""){
            binding.debut1.setText("")
        }
        else{
            binding.debut.text = debut
        }
    }
}